import moment from "moment";
import React, { useEffect, useState } from "react";
import { Calendar, momentLocalizer } from 'react-big-calendar';
import { Body, FormEvent, InputEvent, InputEventText } from "./styled";


const Agenda = () => {
    const [myEventsList, setMyEventsList] = useState([
        {
            'title': 'My event',
            'allDay': false,
            'start': new Date(2022, 6, 10, 10, 0), // 10.00 AM
            'end': new Date(2022, 6, 10, 14, 0), // 2.00 PM 
          }
    ])



    const [title, setTitle] = useState("")


    const [startDay, setStartDay] = useState(0)
    const [startMonth, setStartMonth] = useState(0)
    const [startYear, setStartYear] = useState(0)
    const [startHour, setStartHour] = useState(0)
    const [startMin, setStartMin] = useState(0)

    const [endDay, setEndDay] = useState()
    const [endMonth, setEndMonth] = useState()
    const [endYear, setEndYear] = useState()
    const [endHour, setEndHour] = useState()
    const [endMin, setEndtMin] = useState()

    useEffect(() => {
        console.log(startDay)
    },[startDay])

    const localizer = momentLocalizer(moment)

    const addEvent = () => {
        setMyEventsList(prevState => ({...prevState,
            'title':title,
            'allDay': false,
            'start': new Date(startYear, startMonth, startDay, startHour, startMin),
            'end': new Date(endYear, endMonth, endDay, endHour, endMin )
        }))
    }

    return(
    <Body>
        <Calendar
      localizer={localizer}
      events={myEventsList}
      startAccessor="start"
      endAccessor="end"
      style={{ height: 500 , width: 800}}
        />
    <FormEvent>
        <text>Marcar um evento</text>
        <div>
        <InputEventText></InputEventText>
        </div>
        <text>Inicio</text>
        <div>
        <InputEvent type="number" placeholder="Dia" value={startDay} onChange={(e) => setStartDay(e.target.valueAsNumber)} ></InputEvent>
        <InputEvent type="number" placeholder="Mês" value={startMonth} onChange={(e) => setStartMonth(e.target.valueAsNumber)}></InputEvent>
        <InputEvent type="number" placeholder="Ano" value={startDay} onChange={(e) => setStartDay(e.target.valueAsNumber)}></InputEvent>
        <InputEvent type="number" placeholder="Hora" value={startDay} onChange={(e) => setStartDay(e.target.valueAsNumber)}></InputEvent><text>:</text>
        <InputEvent type="number" placeholder="Minuto" value={startDay} onChange={(e) => setStartDay(e.target.valueAsNumber)}></InputEvent>
        </div>
        <text>Final</text>
        <div>
        <InputEvent type="number" placeholder="Dia"></InputEvent>
        <InputEvent type="number" placeholder="Mês"></InputEvent>
        <InputEvent type="number" placeholder="Ano"></InputEvent>
        <InputEvent type="number" placeholder="Hora"></InputEvent><text>:</text>
        <InputEvent type="number" placeholder="Minuto"></InputEvent>
        </div>
        <button style={{marginBottom: 100}}>Adicionar</button>
    </FormEvent>
    </Body>
    )
}

export default Agenda;
