import moment from "moment";
import React, { useEffect, useState } from "react";
import { Calendar, momentLocalizer } from 'react-big-calendar';
import { Body, FormEvent, InputEvent, InputEventText } from "./styled";


const Agenda = () => {
    const [myEventsList, setMyEventsList] = useState([])

    let Array = [];


    const [title, setTitle] = useState("")


    const [startDay, setStartDay] = useState()
    const [startMonth, setStartMonth] = useState()
    const [startYear, setStartYear] = useState()
    const [startHour, setStartHour] = useState()
    const [startMin, setStartMin] = useState()

    const [endDay, setEndDay] = useState()
    const [endMonth, setEndMonth] = useState()
    const [endYear, setEndYear] = useState()
    const [endHour, setEndHour] = useState()
    const [endMin, setEndtMin] = useState()

    useEffect(() => {
        setMyEventsList(Array)
    },[])

    const localizer = momentLocalizer(moment)

    const addEvent = () => {
        setMyEventsList(oldArray => [...oldArray, {nome: "Leandro"}])
        // Array.push({
        //     title:title,
        //     allDay: false,
        //     start: new Date(startYear, startMonth, startDay, startHour, startMin),
        //     end: new Date(endYear, endMonth, endDay, endHour, endMin )   
        // })
        
    
    }

    return(
    <Body>
        <Calendar
      localizer={localizer}
      events={Array}
      startAccessor="start"
      endAccessor="end"
      style={{ height: 500 , width: 800}}
        />
    <FormEvent>
        <text>Marcar um evento</text>
        <div>
        <InputEventText value={title} onChange={(e) => setTitle(e.target.value)}></InputEventText>
        </div>
        <text>Inicio</text>
        <div>
        <InputEvent type="number" placeholder="Dia" value={startDay} onChange={(e) => setStartDay(e.target.valueAsNumber)} ></InputEvent>
        <InputEvent type="number" placeholder="Mês" value={startMonth} onChange={(e) => setStartMonth(e.target.valueAsNumber)}></InputEvent>
        <InputEvent type="number" placeholder="Ano" value={startYear} onChange={(e) => setStartYear(e.target.valueAsNumber)}></InputEvent>
        <InputEvent type="number" placeholder="Hora" value={startHour} onChange={(e) => setStartHour(e.target.valueAsNumber)}></InputEvent><text>:</text>
        <InputEvent type="number" placeholder="Minuto" value={startMin} onChange={(e) => setStartMin(e.target.valueAsNumber)}></InputEvent>
        </div>
        <text>Final</text>
        <div>
        <InputEvent type="number" placeholder="Dia" value={endDay} onChange={(e) => setEndDay(e.target.valueAsNumber)}></InputEvent>
        <InputEvent type="number" placeholder="Mês" value={endMonth} onChange={(e) => setEndMonth(e.target.valueAsNumber)}></InputEvent>
        <InputEvent type="number" placeholder="Ano" value={endYear} onChange={(e) => setEndYear(e.target.valueAsNumber)}></InputEvent>
        <InputEvent type="number" placeholder="Hora" value={endHour} onChange={(e) => setEndHour(e.target.valueAsNumber)}></InputEvent><text>:</text>
        <InputEvent type="number" placeholder="Minuto" value={endMin} onChange={(e) => setEndtMin(e.target.valueAsNumber)}></InputEvent>
        </div>
        <button style={{marginBottom: 100}} onClick={() => addEvent()}>Adicionar</button>
    </FormEvent>
    </Body>
    )
}

export default Agenda;
