import moment from "moment";
import React, { useState } from "react";
import { Calendar, momentLocalizer } from 'react-big-calendar';
import { Body, FormEvent, InputEvent, InputEventText } from "./styled";


const Agenda = () => {
    const [myEventsList, setMyEventsList] = useState([
        {
            'title': 'My event',
            'allDay': false,
            'start': new Date(2022, 6, 10, 10, 0), // 10.00 AM
            'end': new Date(2022, 6, 10, 14, 0), // 2.00 PM 
          }
    ])

    const [title, setTitle] = useState()

    const localizer = momentLocalizer(moment)

    const addEvent = () => {
        setMyEventsList(prevState => ({...prevState, 'title':title, 'allDay': false, 'start': new Date()}))
    }

    return(
    <Body>
        <Calendar
      localizer={localizer}
      events={myEventsList}
      startAccessor="start"
      endAccessor="end"
      style={{ height: 500 , width: 800}}
        />
    <FormEvent>
        <text>Marcar um evento</text>
        <div>
        <InputEventText></InputEventText>
        </div>
        <text>Inicio</text>
        <div>
        <InputEvent placeholder="Dia"></InputEvent>
        <InputEvent placeholder="Mês"></InputEvent>
        <InputEvent placeholder="Ano"></InputEvent>
        <InputEventText placeholder="Horario Inicial"></InputEventText>
        </div>
        <text>Final</text>
        <div>
        <InputEvent placeholder="Dia"></InputEvent>
        <InputEvent placeholder="Mês"></InputEvent>
        <InputEvent placeholder="Ano"></InputEvent>
        </div>
        <button style={{marginBottom: 100}}>Adicionar</button>
    </FormEvent>
    </Body>
    )
}

export default Agenda;
